INSERT INTO `wp_links` VALUES (12, 'http://reformjudaismmag.org/Articles/index.cfm?id=1019', 'Colonial Cuisine', '', '_blank', 0, 'Appeared in Reform Judaism\'s Summer 2005 issue. If you think it\'s hard to find good produce in the markets today, how do you think you would have fared had you been one of the twenty-three Jewish refugees who arrived in New Amsterdam from Brazil in 1654?', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''); 
