INSERT INTO `wp_links` VALUES (16, 'http://www.cookingwithsheilah.com/', 'Sheilah Kaufman', '', '_blank', 0, 'Sheilah is an energetic cooking instructor based in the D.C. area who also travels around the country teaching kosher cooking classes as well as "fearless, fussless" recipes. If you\'re in her area, see what classes she\'s offering or check out her site for', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''); 
INSERT INTO `wp_links` VALUES (17, 'http://www.jccmanhattan.org/', 'The Jewish Community Center in Manhattan', '', '_blank', 0, 'Check out the classes at the Jcc Manhattan if you\'re in the area. The facilities are very impressive and they offer many classes in Judaic thought, Photography, Parenting, Sports, Ceramics and of course, Cooking! Their kitchen is great.', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''); 
