INSERT INTO `wp_posts` VALUES (309, 2, '2009-12-12 12:18:18', '2009-12-12 19:18:18', '<h4>Subscribe to my free email newsletter</h4>\nEach month I send out a free newsletter containing recipes, suggestions, and tidbits of information about cooking. Fill out and submit the following form in order to be added to my mailing list. After you submit this form, you will be sent an email to confirm that it is actually you requesting a subscription. Once you confirm that you would like to subscribe to my newsletter, you will be sent your first edition.\n\n<table style="padding-bottom: 30px;" border="0" cellspacing="0" cellpadding="1" bgcolor="White">\n<form action="http://www.icebase.com/multidouble.ice" method="post">\n<input name="username" type="hidden" value="magicook" /> \n<input name="notification" type="hidden" value="tina@cookingandmore.com" /> \n<input name="contactemail" type="hidden" value="tina@cookingandmore.com" /> <input name="contactname" type="hidden" value="Cooking and More..." />\n\n<tbody>\n<tr>\n<td><span style="color: black;">\n<table border="0" cellspacing="1" cellpadding="1">\n<tbody>\n<tr>\n<td><span style="color: black;">Email<span style="color: #8b0000;"><sup>*</sup></span>\n</span></td>\n<td><input maxlength="120" name="Email" size="20" type="text" /></td>\n</tr>\n<tr>\n<td><span style="color: black;">Name</span></td>\n<td><input maxlength="120" name="Name" size="20" type="text" /></td>\n</tr>\n<tr>\n<td><span style="color: black;">Company</span></td>\n<td><input maxlength="120" name="Company" size="20" type="text" /></td>\n</tr>\n</tbody></table>\n<table border="0" cellspacing="1" cellpadding="1">\n<tbody>\n<tr>\n<td><input name="list2" type="checkbox" value="Hallal" /> Hallal cooking tips</td>\n</tr>\n<tr>\n<td><input name="list3" type="checkbox" value="Kosher" /> Kosher cooking tips</td>\n</tr>\n<tr>\n<td><input name="list4" type="hidden" value="Newsletter" /> <input name="list5" type="checkbox" value="Vegetarian" /> Vegetarian cooking tips</td