INSERT INTO `wp_product_categories` VALUES (4, 1, 'Greeting Cards', 'greeting-cards', 'Same layout as the note cards with the recipe on the interior left side, but these cards contain a greeting pertinent to the recipe.  For example, the sweet, Eastern European recipe for Teiglach eaten at Rosh Hashanah has the greeting, "Wishing you a sweet and joyous New Year."', 'salad_card.jpg', '0', '1', 0, 0, '', '', ''); 
INSERT INTO `wp_product_categories` VALUES (3, 1, 'Note Cards', 'note-cards', 'Each card contains a tempting photo of a unique dish with the recipe enclosed on the inside of the card.  Tidbits of culinary history or technique are listed on the back of the card, and after the recipient reads the note you enclose, the card can be cut in half to retain the recipe card.  The cards are more than a friendly note; they are truly a gift.', 'caponata_card.jpg', '0', '1', 0, 0, '', '', ''); 
