INSERT INTO `wp_product_categories` VALUES (8, 1, 'Boxed Sets', 'boxed-sets', 'Boxed sets are an assortment of 2 each of 5 designs for a total of 10 cards with 11 envelopes (1 as a bonus).  There are 11 sets of cards covering Sweets, Savories, Jewish Holidays and non-sectarian holiday cuisine.  Each boxed set comes in a clear plastic box with an insert on the back showing the box’s contents.  With the exception of the Rosh Hashanah sets, which contain greetings, all other sets contain note cards with sufficient room for your own note.  Cards are computer-compatible for printing invitations or announcements.', 'dulcedemanzanathumb.JPG', '0', '1', 0, 0, '', '', ''); 
